DROP TABLE IF EXISTS lke_questions;
DROP TABLE IF EXISTS lke_answers;

CREATE TABLE lke_questions (
     id INT PRIMARY KEY NOT NULL,
     category_id INT,
     number VARCHAR(30),
     question_val TEXT,
     update_date INT
);

CREATE TABLE lke_answers (
    id INT PRIMARY KEY NOT NULL,
    question_id INT,
    answer TEXT,
    correct VARCHAR(10)
);



